const initialState = {
    popularMovies: [],
    newMovies: [],
    upcomingMovies: [],
}


const movieReducer = (state=initialState, action) =>{
    switch(action.type){
        case "FETCH_MOVIES" :
            return {
                ...state,
                popular: action.payload.popular}
            default: 
            return{...state}
    }
}

export default movieReducer
