import axios from 'axios'
import {popularMovieApi, upcomingMovieApi} from '../Api/api'

//ACTIONS

export const loadMovies = () => async (dispatch) =>{

    const popularData = await axios.get(popularMovieApi);
    dispatch({
        type: "FETCH_MOVIES",
        payload: {
            popularMovies: popularData.data,
        },
    });

};
