import React from 'react'
import styled from 'styled-components'
import '../assets/fontAwesome';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlay } from '@fortawesome/free-solid-svg-icons';
import { useDispatch } from 'react-redux';
import { loadDetail } from '../actions/detailsActions';
import {Link} from 'react-router-dom'
const Movies = ({title,image, id})=>{

    const finalImage = `https://image.tmdb.org/t/p/w500${image}`

    const dispatch = useDispatch()

    const loadDetailhandler = ()=>{
            document.body.style.overflow = 'hidden';
                dispatch(loadDetail(id))
    }
    return(
        <Movie onClick={loadDetailhandler}>
            <Link to={`/game/${id}`}>
            <h1>{title}</h1>
            <img src={finalImage} alt={title}/>
            </Link>
        </Movie>
    )
}

const Movie = styled.div`
width: 100%;
height: 50vh;
border-radius: 1rem;
background-color: black;
display: flex;
flex-direction: column;
justify-content: space-between;
overflow: hidden;
cursor: pointer;
position: relative;
box-shadow: inset 0px -30px 30px 0px rgba(0,0,0,0.75);
&:hover{
    box-shadow: 0px 0px 50px 0px rgba(71,204,160,0.2);


}

img{
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    z-index: 1;
    display: block;
}
h1{
    font-size: 2rem;
    text-align: center;
    color: #47CCA0;
    z-index: 4;
}
`

const Icon = styled(FontAwesomeIcon)`
color: white;
position: absolute;
top: 65%;
left: 50%;
transform: translate(-50%, -50%);
z-index: 2;
font-size: 2rem;


`
export default Movies
