import axios from 'axios'
import {popularMovieApi} from '../Api/api'

//ACTIONS

export const loadMovies = () => async (dispatch) =>{

    const popularData = await axios.get(popularMovieApi);
    dispatch({
        type: "FETCH_MOVIES",
        payload: {
            popular: popularData,
        },
    });

};
