import React from 'react'
import styled from 'styled-components'
import {motion} from 'framer-motion'

const MainMovie = ({title, release, image, overview, id, rating}) =>{
    const finalImage = `https://image.tmdb.org/t/p/w500${image}`
    return(
        <Movie>
            <MovieBackground src={finalImage} />
            <Title>
                {title}
            </Title>
        </Movie>
    )
}

const Movie = styled(motion.div)`
width: 80%;
height: 50vh;
margin: 0 auto;
margin-top: 5rem;
position: relative;
cursor: pointer;
img{
    &:hover{
        width: 120%;
    }
}
`

const MovieBackground = styled(motion.img)`
width: 100%;
height: 100%;
object-fit: cover;
position: absolute;
top: 0;
left: 0;
z-index: -1;

`
const Title = styled(motion.h1)`
color: white;
text-align: center;
font-size: 2rem;
`
export default MainMovie
